import React, { Component } from 'react';
import Naved from './components/nav';
import Home from './home';
import Projects from './components/projects';
import Board from './components/board';
import './components/styles/syles.css';
import fire from "./fire.js";
import PrivateRoute from "./PrivateRoute";
import { BrowserRouter as Router, Route } from "react-router-dom";
import './App.css';
import LogIn from "./Login";

class App extends Component {

    state = { loading: true, authenticated: false, currentUser: null};

    componentWillMount() {
        fire.auth().onAuthStateChanged(user => {
            if (user) {
                this.setState({
                    authenticated: true,
                    currentUser: user,
                    loading: false
                });
            } else {
                this.setState( {
                    authenticated: false,
                    currentUser: null,
                    loading: false
                });
            }
        });
    }

    render() {
        // eslint-disable-next-line
        const { authenticated, loading, currentUser } = this.state;

        if  (loading) {
            return <p>Loading..</p>;
        }

        return (
            <Router>
                <div>
                    <PrivateRoute exact path="/" component={Home} authenticated={authenticated}/>
                    <Route exact path="/login" component={(LogIn)}/>
                    <Route path="/projects" component={Projects}  authenticated={authenticated}/>
                    <Route path="/board" component={Board} authenticated={authenticated}/>
                </div>
            </Router>
        );
    }
}

export default App;
