import React, { Component } from 'react';
import './styles/bootstrap.css';
import './styles/bootstrap-grid.css';
import './styles/bootstrap-reboot.css';
import './styles/syles.css';
import './js/dnd.js';
import Images from './images';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import firebase from "../fire";
import './styles/syles.css';
import styled from 'styled-components';


class Board extends Component {

    constructor(props) {
        super(props);
        this.ref = firebase.firestore().collection('users');
        this.ref = firebase.firestore().collection('cart');
        this.state = {
            ShopItems: [],
            cart: [],
            item: {},
            key: '',
            name: '',
            price: '',
            stock: '',
            image: '',
        }
    };


    onChange = (e) => {
        const state = this.state;
        state[e.target.name] = e.target.value;
        this.setState({item:state});
    };

    onSubmit = (e) => {
        e.preventDefault();

        const { name, stock, price, image } = this.state;

        const updateRef = firebase.firestore().collection('cart').doc(this.state.key);
        updateRef.set({
            name,
            stock,
            price,
            image


        }).then((itemRef) => {
            this.setState({
                key: '',
                name: '',
                stock: '',
                price: '',
                image: '',
            });
            this.props.history.push("/show/"+this.props.match.params.id)
        })
            .catch((error) => {
                console.error("Error adding document: ", error);
            });
    };

    componentDidMount() {
        const ref = firebase.firestore().collection('ShopItems').doc(this.props.match.params.id);

        ref.get().then((doc) => {
            if (doc.exists) {
                this.setState({
                    item: doc.data(),
                    key: doc.id,
                    isLoading: false
                });
            } else {
                console.log("No such document!");
            }
        });

        const itemRef = firebase.firestore().collection('cart').doc(this.props.match.params.id);

        itemRef.get().then((doc) => {
            if (doc.exists) {
                const item = doc.data();
                this.setState({
                    key: doc.id,
                    name: item.name,
                    stock: item.stock,
                    price: item.price,
                    image: item.image,
                });
            } else {
                console.log("No such document!");
            }
        });

        this.unsubscribe = this.ref.onSnapshot(this.onCollectionUpdate);

    }

    onCollectionUpdate = (querySnapshot) => {
        const cart = [];
        querySnapshot.forEach((doc) => {
            const { name, price, stock, image } = doc.data();
            cart.push({
                key: doc.id,
                doc, // DocumentSnapshot
                name,
                price,
                stock,
                image,
            });
        });

        this.setState({
            cart
        });
    };

    delete(id){
        firebase.firestore().collection('ShopItems').doc(id).delete().then(() => {
            console.log("Document successfully deleted!");
            this.props.history.push("/")
        }).catch((error) => {
            console.error("Error removing document: ", error);
        });
    }


    toggle() {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }





    render() {
        return (
            <div>
                <div className="container-fluid">
                    <div className="row project-head">
                        <div className="p-title nav-text"><span className="project">Collection: </span> Procrastinate
                        </div>
                    </div>
                    <div className="margin-help">
                        <div className="row">
                            <div className="col-md-4">
                                <div className="card">
                                    <div className="card-body card-body-height" id="div1" onDrop="drop(event)"
                                         onDragOver="allowDrop(event)">
                                        <h5 className="card-title">To Do: Avoid Making a To-Do List (2)<img className="tools plus pointer" src={Images.plus}/></h5>
                                        <div className="underline"></div>

                                        <div className="col list-item pointer" onClick={this.toggle}
                                             draggable="true" onDragStart="drag(event)" id="drag1">

                                            <div className="col">
                                                <div className="list-title">
                                                    Avoid Listing Tasks
                                                </div>
                                            </div>
                                            <div className="col">
