import React, { Component } from 'react';
import Images from './images';
import firebase from "../fire";
import {NavLink as RRNavLink} from "react-router-dom";
import {NavLink} from 'react-router-dom';
import './styles/syles.css';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import styled from 'styled-components';


class Projects extends Component {
    constructor(props) {
        super(props);
        this.ref = firebase.firestore().collection('users');
        this.state = {
            users: [],
        };

    }

    onCollectionUpdate = (querySnapshot) => {
        const project3 = [];
        querySnapshot.forEach((doc) => {
            const { project1tasks  } = doc.data();
            project1.push({
                key: doc.id,
                doc, // DocumentSnapshot
                project3tasks,
            });
        });
        this.setState({
            project3
        });
    };

    componentDidMount() {
        this.unsubscribe = this.ref.onSnapshot(this.onCollectionUpdate);
    }

    render() {
        const stockAmount = this.state.stock;

        return (
            <div className="container">
                <div className="panel panel-default">
                    <div className="panel-heading">
                        <h3 className="panel-title">
                            Projects - Looks like you should get started...
                        </h3>
                    </div>
                    <div className="panel-body">
                        <table className="table table-stripe">
                            <tbody>
                            {this.state.users.map(item =>
                                <tr>
                                    <td><NavLink tag={RRNavLink} className="nav-link" to={`/board/${item.key}`}></NavLink>{item.name}</td>
                                </tr>
                            )}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        );
    }
}

export default Projects;
