import React, { Component } from 'react';
import Images from './images';
import firebase from "../fire";
import {Link, NavLink as RRNavLink} from "react-router-dom";
import {NavLink} from 'react-router-dom';
import './styles/syles.css';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import styled from 'styled-components';

const Div1 = styled.div`

   color: red;
 }
`;

class Cart extends Component {
    constructor(props) {
        super(props);
        this.ref = firebase.firestore().collection('cart');
        this.state = {
            cart: [],
            item: {},
            key: '',
            name: '',
            price: '',
            stock: '',
            image: '',
            qty: '',
        };
    }



    updateInput = e => {
        this.setState({
            [e.target.name]: e.target.value
        });
    };

    onChange = (e) => {
        const state = this.state;
        state[e.target.name] = e.target.value;
        this.setState({item:state});
    };


    onCollectionUpdate = (querySnapshot) => {
        const cart = [];
        querySnapshot.forEach((doc) => {
            const { name, price, stock, image, qty } = doc.data();
            cart.push({
                key: doc.id,
                doc, // DocumentSnapshot
                name,
                price,
                stock,
                image,
                qty,
            });
        });
        this.setState({
            cart
        });
    };

    delete(id){
        firebase.firestore().collection('cart').doc(id).delete().then(() => {
            console.log("Document successfully deleted!");
            this.props.history.push("/")
        }).catch((error) => {
            console.error("Error removing document: ", error);
        });
    }

    componentDidMount() {
        this.unsubscribe = this.ref.onSnapshot(this.onCollectionUpdate);

    }



    render() {
        const qtyAmnt = this.state.item.stock;




        return (
            <div className="container">
                <div className="panel panel-default">
                    <div className="panel-heading">
                        <h3 className="panel-title">
                            Your Cart
                        </h3>
                    </div>
                    <div className="panel-body">
                        <table className="table table-stripe">
                            <thead>
                            <tr>
                                <th>Name</th>
                                <th>Image</th>
                                <th>In Stock</th>
                                <th>Quantity</th>
                                <th>Price</th>
                                <th>Remove</th>
                            </tr>
                            </thead>
                            <tbody>
                            {this.state.cart.map(item =>
                                <tr>
                                    <td>{item.name}</td>
                                    <td><NavLink tag={RRNavLink} className="nav-link" to={`/show/${item.key}`}><img src={item.image} alt="No Image to Display"   /></NavLink></td>
                                    <td>{item.stock}&nbsp;{item.stock < 10  ? <Div1>Warning! Low Stock</Div1> : '' }</td>
                                    <td>{item.qty}</td>
                                    <td>${item.price}</td>
                                    <td>
                                        <button onClick={this.delete.bind(this, item.key)}
                                                className="btn btn-danger">Delete
                                        </button></td>
                                </tr>
                            )}
                            {this.state.cart.map(item=>
                                <tr>
                                    <td>&nbsp;</td>
                                    <td>Quantity & Total: </td>
                                    <td></td>
                                    <td>{item.qty}</td>
                                    <td>${item.price}</td>
                                    <td></td>
                                    <td></td>

                                </tr>
                            )}
                            <tr>
                                <td>&nbsp;</td>
                                <td>&nbsp;</td>
                                <td>&nbsp;</td>
                                <td></td>
                                <td></td>
                                <td></td>
                                <td>
                                    <Button  color="primary" onClick={this.toggle}>Purchase</Button>&nbsp;
                                    <br/>
                                    <br/>
                                    <h4><Link to={`/store`} class="btn btn-primary">Back to Store</Link></h4>
                                </td>

                            </tr>
                            </tbody>
                        </table>
                    </div>
                </div>

            </div>
        );
    }
}

export default Cart;
