import React, { Component } from 'react';
import Images from './images';
import firebase from "../fire";
import {NavLink as RRNavLink} from "react-router-dom";
import {NavLink} from 'react-router-dom';
import './styles/syles.css';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import styled from 'styled-components';

const Div1 = styled.div`

   background-color: red;
 }
`;

const Div2 = styled.div`

   background-color: blue;
 }
`;

const Div3 = styled.div`

   background-color: white;
 }
`;

class Store extends Component {
    constructor(props) {
        super(props);
        this.ref = firebase.firestore().collection('ShopItems');
        this.state = {
            ShopItems: [],
            stock: '',
            modal: false
        };

        this.toggle = this.toggle.bind(this);
    }

    toggle() {
        this.setState(prevState => ({
            modal: !prevState.modal
        }));
    }

    onCollectionUpdate = (querySnapshot) => {
        const ShopItems = [];
        querySnapshot.forEach((doc) => {
            const { name, price, stock, image } = doc.data();
            ShopItems.push({
                key: doc.id,
                doc, // DocumentSnapshot
                name,
                price,
                stock,
                image,
            });
        });
        this.setState({
            ShopItems
        });
    };

    componentDidMount() {
        this.unsubscribe = this.ref.onSnapshot(this.onCollectionUpdate);
    }

    render() {
        const stockAmount = this.state.stock;

        return (


            <div className="container">
                <div className="panel panel-default">
                    <div className="panel-heading">
                        <h3 className="panel-title">
                            Your Projects - Looks like you better get started...
                        </h3>
                    </div>
                    <div className="panel-body">
                        <table className="table table-stripe">
                            <thead>
                            <tr>

                                <th>Image </th>
                            </tr>
                            </thead>
                            <tbody>
                            {this.state.ShopItems.map(item =>
                                <tr>
                                    <td><span>{ item.stock > 100 ? <Div1>&nbsp; <br/> <br/> <br/> <br/> </Div1> : item.stock < 10 ? <Div2>&nbsp; <br/> <br/> <br/> <br/> </Div2> : <Div3>&nbsp; <br/> <br/> <br/> <br/> </Div3>}</span><NavLink tag={RRNavLink} className="nav-link" to={`/show/${item.key}`}><img src={item.image} alt="No Image to Display"   /></NavLink></td>
                                </tr>
                            )}
                            </tbody>
                        </table>
                    </div>
                </div>
                {this.state.ShopItems.map(item =>
                    <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
                        <ModalHeader toggle={this.toggle}>{item.name}</ModalHeader>
                        <ModalBody>
                            <img src={item.image} alt="No Image to Display"  onClick={this.toggle}  />
                            <dt>Stock:</dt>
                            <dd>{item.stock}</dd>
                            <dt>Price:</dt>
                            <dd>{item.price}</dd>
                        </ModalBody>
                        <ModalFooter>
                            <Button color="primary" onClick={this.toggle}>Add to Cart</Button>{' '}
                            <Button color="secondary" onClick={this.toggle}>Cancel</Button>
                        </ModalFooter>
                    </Modal>
                )}

            </div>
        );
    }
}

export default Store;
